package computeArea;

public class ComputeAreaCircle {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		double radius = 20;
		double area;
		
		area = radius * radius * 3.1416;
		
		System.out.print("The area for the circle of radius" +radius  +  "is"  +area);

	}

}
